mapper.select:
select * from tab_country where 1=1

		and country_no = #{country_no}
	
order by country_name

 select * from tab_city where 1=1
 
 	
 		and country_no = #{country_no}
 	
 	
 		and city_no = #{city_no}
 	

 order by city_name

  	 select * from tab_area where 1=1

  	 	
